import Rails from "@rails/ujs";
import "bootstrap"; // Import Bootstrap JavaScript

Rails.start();

// app/packs/scripts/application.js

document.addEventListener('DOMContentLoaded', function() {
  const addFilterBtn = document.getElementById('addFilterBtn');
  if (addFilterBtn) {
    addFilterBtn.addEventListener('click', function(event) {
      event.preventDefault();
      // Create a new dropdown element with options
      createAndAppendFilterDropdown();
    });
  }
});

// Function to create a new filter dropdown and populate it with options
function createAndAppendFilterDropdown() {
  // Create a new dropdown container
  const newFilterDropdown = document.createElement('div');
  newFilterDropdown.classList.add('form-group', 'col-md-4', 'mb-2');

  const label = document.createElement('label');
  label.textContent = 'Parameter:';

  const select = document.createElement('select');
  select.setAttribute('name', 'parameter_id');
  select.classList.add('form-select');

  // Fetch options from the backend
  fetch('/parameters')
    .then(response => response.json())
    .then(parameters => {
      // Populate the dropdown with options
      parameters.forEach(parameter => {
        const option = document.createElement('option');
        option.setAttribute('value', parameter.id);
        option.textContent = parameter.name;
        select.appendChild(option);
      });
    })
    .catch(error => {
      console.error('Error fetching parameters:', error);
    });

  newFilterDropdown.appendChild(label);
  newFilterDropdown.appendChild(select);

  // Get the filter options container
  const filterOptionsContainer = document.getElementById('filterOptions').querySelector('.card-body');

  // Create a new row if one doesn't exist already
  let row = filterOptionsContainer.querySelector('.row');
  if (!row) {
    row = document.createElement('div');
    row.classList.add('row');
    filterOptionsContainer.appendChild(row);
  }

  // Append the new dropdown container to the row
  row.appendChild(newFilterDropdown);
}
